add_action( 'wp_footer', 'hybrid_custom_footer_scripts' );

/**
 * Echo the footer scripts, defined in Theme Settings.
 */
function hybrid_custom_footer_scripts() {

  echo hybrid_get_setting( 'footer_scripts' );

}


/* footer insert to the footer. */
add_action( hybrid_get_prefix() . '_footer', 'hybrid_footer_insert' );

function hybrid_footer_insert() {
  ?>
    <div class="footer-content footer-insert">
      <?php hybrid_footer_content(); ?>
    </div>
  <?php
}